const Icon: React.FC<IconProps> = ({
  type,
  size,
  width,
  height,
  color = 'var(--theme_primary_color_black)',
  rotate = '0',
  className = '',
  style,
  onClick,
}) => {
  const finalWidth = width || size || '24px';
  const finalHeight = height || size || '24px';

  return (
    <svg
      style={{
        transform: `rotate(${rotate}deg)`,
        ...style,
      }}
      onClick={onClick}
      className={className}
      width={finalWidth}
      height={finalHeight}
      viewBox="0 0 24 24"
      fill="none"
      xmlns="http://www.w3.org/2000/svg"
      color={color}
    >
      {type && icons[type]}
    </svg>
  );
};

export default Icon;
